    public static ArrayList<Kucing> getListData(){
        ArrayList<Kucing> list = new ArrayList<>();
        for(String[] aData : data){
            Kucing kucing = new Kucing();
            kucing.setId(Integer.parseInt(aData[0]));
            kucing.setName(aData[1]);
            kucing.setDesc(aData[2]);
            kucing.setPhoto(aData[3]);

            list.add(kucing);
        }
        return list;
    }
